{"settings":"{
  "workbench.iconTheme": "material-icon-theme",
  "editor.fontSize": 20,
  "workbench.preferredDarkColorTheme": "Carbonfox",
  "cmake.pinnedCommands": [
    "workbench.action.tasks.configureTaskRunner",
    "workbench.action.tasks.runTask"
  ],
  "cSpell.userWords": [
    "Framebuffer",
    "glfw",
    "TAPI",
    "Thalune",
    "Thauma"
  ],
  "git.autofetch": true,
  "[javascript]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[html]": {
    "editor.defaultFormatter": "vscode.html-language-features"
  },
  "gitlens.views.branches.files.layout": "list",
  "[json]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[typescript]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "editor.cursorStyle": "block",
  "workbench.colorTheme": "Carbonfox",
  "editor.renderWhitespace": "boundary",
  "editor.cursorBlinking": "phase",
  "editor.cursorSmoothCaretAnimation": "on",
  "editor.cursorSurroundingLinesStyle": "all",
  "window.customTitleBarVisibility": "auto",
  "workbench.activityBar.location": "hidden",
  "workbench.preferredHighContrastColorTheme": "Duskfox",
  "workbench.sideBar.location": "right",
  "workbench.activityBar.iconClickBehavior": "focus",
  "editor.lineHeight": 1.3,
  "editor.lineNumbers": "relative",
  "workbench.editor.autoLockGroups": {
    "mainThreadWebview-simpleBrowser.view": false
  },
  "workbench.editor.highlightModifiedTabs": true,
  "workbench.editor.pinnedTabSizing": "shrink",
  "workbench.editor.scrollToSwitchTabs": true,
  "workbench.editor.titleScrollbarSizing": "large",
  "editor.minimap.enabled": false,
  "workbench.editor.enablePreview": false,
  "tabnine.experimentalAutoImports": true,
  "workbench.colorCustomizations": {},
  "files.associations": {
    "*.html": "html"
  },
  "prettier.bracketSameLine": true,
  "prettier.bracketSpacing": false,
  "prettier.printWidth": 120,
  "[scss]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[php]": {
    "editor.defaultFormatter": "bmewburn.vscode-intelephense-client"
  },
  "window.menuBarVisibility": "compact",
  "[vue]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "github.copilot.nextEditSuggestions.enabled": true,
  "files.autoSave": "afterDelay",
  "extensions.experimental.affinity": {
    "asvetliakov.vscode-neovim": 1
  },
  "editor.tabSize": 2,
  "editor.detectIndentation": false,
  "C_Cpp.default.cppStandard": "c++23",
  "C_Cpp.default.cStandard": "c23",
  "gitlens.ai.model": "vscode",
  "gitlens.ai.vscode.model": "copilot:gpt-4.1",
  "github.copilot.enable": {
    "*": true,
    "plaintext": true,
    "markdown": false,
    "scminput": false
  }
}"}
